import streamlit as st

# ini adalah halaman utama
st.title('aplikasi perhitungan luas bangun datar')
st.header('ini buatan anak SI')

menu = st.sidebar.selectbox( 'pilih aplikasi',['luas Persegi', 'luas Persegi Panjang', 'luas Segitiga', 'luas Lingkaran'])
if menu == 'luas Persegi':
    st.image('data:image/png;base64,')
    st.subheader('aplikasi menghitung luas Persegi')
    sisi = st.number_input('masukkan panjang sisi')
    def luas_persegi(sisi):
        return sisi * sisi
    if st.button('hitung luas Persegi'):
        luas = sisi * sisi
        st.success(f'luas Persegi adalah {luas}')
    

elif menu == 'luas Persegi Panjang':
    st.image('data:image/jpeg;base64,')
    st.subheader('aplikasi menghitung luas Persegi Panjang')
    panjang = st.number_input('masukkan panjang')
    lebar = st.number_input('masukkan lebar')
    def luas_persegi_panjang(panjang, lebar):
        return panjang * lebar
    if st.button('hitung luas Persegi Panjang'):
        luas = panjang * lebar
        st.success(f'luas Persegi Panjang adalah {luas}')
        

elif menu == 'luas Segitiga':
    st.image('data:image/png;base64,')
    st.subheader('aplikasi menghitung luas Segitiga')
    alas = st.number_input('masukkan alas')
    tinggi = st.number_input('masukkan tinggi')
    def luas_segitiga(alas, tinggi):
        return 0.5 * alas * tinggi
    if st.button('hitung luas Segitiga'):
        luas = 0.5 * alas * tinggi
        st.success(f'luas Segitiga adalah {luas}')

elif menu == 'luas Lingkaran':
    st.image('https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTK-WuSmG2R5lH5p8_wZLvTBa86Ppcmy_WQ6g&s')
    st.subheader('aplikasi menghitung luas Lingkaran')
    radius = st.number_input('masukkan jari-jari')
    def luas_lingkaran(radius):
        return 3.14 * radius * radius
    if st.button('hitung luas Lingkaran'):
        luas = 3.14 * radius * radius
        st.success(f'luas Lingkaran adalah {luas}')

st.sidebar.info('aplikasi sederhana untuk menghitung luas bangun datar')
st.sidebar.success('selamat menggunakan aplikasi ini')
